import { useLocation } from 'react-router-dom';
import '../style/bandeau.scss';
import ImgBandeau from '../assets/images/Image source 1.png';
import ImgAbout from '../assets/images/Image source 2.png';


export default function Bandeau () {
    const location = useLocation();

    let image, altText;
    if (location.pathname === '/') {
        image = ImgBandeau;
    } else if (location.pathname === '/src/pages/About') {
        image = ImgAbout;
        altText = 'dfv';
    }
    return (
        <div className='bandeau'>
            <div className='img-bandeau'>
            <img src={image} alt={altText}/>
            </div>
    <div className='text-bandeau'><h1>Chez vous, partout et ailleurs</h1></div>
    </div>
)
};
