import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronUp } from '@fortawesome/free-solid-svg-icons';
import '../style/collapse.scss';

export default function Collapse({ title, content, extraClassName }) {
  const [isOpen, setIsOpen] = useState(false);

  const handleToggle = () => {
    event.stopPropagation();
    setIsOpen(!isOpen);
  };

  return (
    <div className={`bar-container ${isOpen ? 'open' : ''} ${extraClassName}`}>
      <div className="bar" onClick={handleToggle}>
        <div className='title'>
          <h3>{title}</h3>
          <FontAwesomeIcon icon={faChevronUp} className={`arrow ${isOpen ? 'rotateDown' : 'rotateUp'}`} />
        </div>
      </div>
      <div className={`expandable-text ${isOpen ? 'expanded' : ''}`}> 
        {Array.isArray(content) ? content.map((item, index) => {
          return (
            <p key={index}>{item}</p>
          )
        }) : <p>{content}</p>}
      </div>
    </div>
  );
}
