import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Datas from '../data/data';
import Slider from '../components/Slider';
import Error from "./Error";
export default function Logements() {

    
	const [imgSlider, setImgSlider] = useState([]);

	const { id } = useParams();
	const dataCurrentLogements = Datas.find(data => data.id === id);

	useEffect(() => {
		if (dataCurrentLogements) {
			setImgSlider(dataCurrentLogements.pictures);
		
          
        }
	}, [id, dataCurrentLogements]);

	if (!dataCurrentLogements) {
		return <Error/>	}



        const name = dataCurrentLogements.host.name.split(' ');
       

        return (
            <div className='accomodation_wrapper'>
                <Slider imageSlider={imgSlider} />
                <main className="accomodation">
                    <div className="accomodation_content">
                        <div className="accomodation_content_infos">
                            <h1>{dataCurrentLogements.title}</h1>
                            <p>{dataCurrentLogements.location}</p>
                            
                        </div>
                        <div className="accomodation_content_host">
                            <div>
                                <div className='accomodation_content_host_name'>
                                    <span>{name[0]}</span>
                                    <span>{name[1]}</span>
                                </div>
                                <img src={dataCurrentLogements.host.picture} alt="host of this accomodation" />
                            </div>
    
                            
                        </div>
                    </div>
                    <div className="accomodation_collapse">
                       
                    </div>
                </main>
            
            </div>
        )
    }
