import React, { useState, useEffect } from 'react';
import Datas from '../data/data';
import '../style/Slider.scss';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronLeft, faChevronRight, faStar as fullStar } from '@fortawesome/free-solid-svg-icons';
import { faStar as emptyStar } from '@fortawesome/free-solid-svg-icons';
import Error from '../pages/Error';

export default function Slider({ id }) {
    const [imageSlider, setImageSlider] = useState([]);
    const [dataCurrentAccomodation, setDataCurrentAccomodation] = useState(null);

    useEffect(() => {
        const data = Datas.find(data => data.id === id);
        setDataCurrentAccomodation(data);
        if (data) {
            setImageSlider(data.pictures);
        }
    }, [id]);

    if (!dataCurrentAccomodation) {
        return <Error />;
    }

   
    
    const nextSlide = () => {
        setIndex((prevIndex) => (prevIndex + 1) % imageSlider.length);
    };

    const prevSlide = () => {
        setIndex((prevIndex) => (prevIndex - 1 + imageSlider.length) % imageSlider.length);
    };

    const Rating = ({ rating }) => {
        const totalStars = 5;
        let stars = [];

        for(let i = 0; i < totalStars; i++) { 
            if(i < rating) {
                stars.push(<FontAwesomeIcon className="star filled" key={i} icon={fullStar} />); // étoile remplie
            } else {
                stars.push(<FontAwesomeIcon className="star empty" key={i} icon={emptyStar} />); // étoile grise
            }
        }
        return <div className='rating'>{stars}</div>;
    };

    return (
        <main>
            <div className='slider'>
                <img className= 'slider-img' src={imageSlider[index]} alt={dataCurrentAccomodation.title} />
                <div className='slider-title-container'>
                    <h2 id='title'>{dataCurrentAccomodation.title}</h2>
                    <p id='host'>{dataCurrentAccomodation.host.name}</p>
                </div>
                <p id='location'>{dataCurrentAccomodation.location}</p>
                <FontAwesomeIcon className='chevron chevron-left' icon={faChevronLeft} onClick={prevSlide} />
                <FontAwesomeIcon className='chevron chevron-right' icon={faChevronRight} onClick={nextSlide} />
                <div id='tags'>
                {dataCurrentAccomodation.tags.map((tag, i) => (
                    <p id='tag-name' key={i}>{tag}</p>
                ))}
                  <Rating rating={dataCurrentAccomodation.rating} />
                </div>
          
            </div>
        </main>
    );
}
