import React, { useState } from 'react';
import { FontAWesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronDown, faChevronRight } from '@fortawesome/free-solid-svg-icons';

 function ExpandableBar({ title, children }) {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <div className="expandable-bar">
      <div className="title">  
      <h2 onClick={() => setIsOpen(!isOpen)} >
        {title}<div className="arrow"><span>{isOpen ? '▼' : '►'}</span>
        </div>
      </h2>
      </div>
      {isOpen && <div className="content">{children}</div>}
    </div>
  );
}


export default function ExpBar() {
  return (
    <div className="bar">
      <ExpandableBar title="Fiabilité">
        <p>Les annonces postées sur Kasa garantissent une fiabilité totale...</p>
      </ExpandableBar>
      <ExpandableBar title="Respect">
        <p>La bienveillance fait partie des valeurs fondatrices de Kasa...</p>
      </ExpandableBar>
      <ExpandableBar title="Service">
        <p>La sécurité est la priorité de Kasa...</p>
      </ExpandableBar>
      <ExpandableBar title="Sécurité">

      </ExpandableBar>
    </div>
  );
}
