import React, { useState } from 'react';
import Datas from '../data/data';
import '../style/Slider.scss';

export default function Slider() {
    const [index, setIndex] = useState(0);

    const nextSlide = () => {
        setIndex((prevIndex) => (prevIndex + 1) % Datas.length);
    };

    const prevSlide = () => {
        setIndex((prevIndex) => (prevIndex - 1 + Datas.length) % Datas.length);
    };

    return (
        <main>
            <button onClick={prevSlide}>Précédent</button>
            <img className= 'img-slider' src={Datas[index].cover} alt={Datas[index].title} />
            
                <h2>{Datas[index].title}</h2>
            
            <button onClick={nextSlide}>Suivant</button>
        </main>
    );
}
