import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronDown, faChevronUp } from '@fortawesome/free-solid-svg-icons';



 function ExpandableBar({ title, children }) {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <div className="expandable-bar">
      <div className="title">  
      <h2 onClick={() => setIsOpen(!isOpen)} >
        {title}<div>
        <FontAwesomeIcon icon={isOpen ? faChevronDown : faChevronUp} className="arrow" />
        </div>
      </h2>
      </div>
      {isOpen && <div className="content">{children}</div>}
    </div>
  );
}


export default function ExpBar() {
  return (
    <div className="bar">
      <ExpandableBar title="Fiabilité">
        <p>Les annonces postées sur Kasa garantissent une fiabilité totale. Les photos sont conformes aux logements, et toutes les informations sont régulièrement vérifiées par nos équipes.</p>
      </ExpandableBar>
      <ExpandableBar title="Respect">
        <p>La bienveillance fait partie des valeurs fandatrices de Kasa. Tout comportement discriminatoire ou de perturbation du voisinage entraînera une exclusion de notre plateforme.</p>
      </ExpandableBar>
      <ExpandableBar title="Service">
        <p>La bienveillance fait partie des valeurs fondatrices de Kasa. Tout comportement discriminatoire ou de perturbation du voisinage entraînera une exclusion de notre plateforme.</p>
      </ExpandableBar>
      <ExpandableBar title="Sécurité">
        <p>La sécurité est la priorité de Kasa. Aussi bien pour nos hôtes que pour les voyageurs, chaque logement correspond aux critères de sécurité établis par nos services. En laissant une note aussi bien à l'hôte qu'au 
            locataire, cela permet à nos équipes de vérifier que les standars
        </p>
      </ExpandableBar>
    </div>
  );
}
