import React, { useState } from 'react';
import Datas from '../data/data';
import '../style/Slider.scss';

export default function Slider() {
    const [index, setIndex] = useState(0);

    const nextSlide = () => {
        setIndex((prevIndex) => (prevIndex + 1) % Datas.length);
    };

    const prevSlide = () => {
        setIndex((prevIndex) => (prevIndex - 1 + Datas.length) % Datas.length);
    };

    return (
        <main>
            <div className='slider'>
                <img className= 'img-slider' src={Datas[index].cover} alt={Datas[index].title} />
                <div>
                    <div><h2 className='h2-slider'>{Datas[index].title}</h2> </div>
                    <div></div>
                </div>
                
                <div className='chevron chevron-left' onClick={prevSlide}>&lt;</div>
                <div className='chevron chevron-right' onClick={nextSlide}>&gt;</div>
            </div>
        </main>
    );
}
