import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronUp } from '@fortawesome/free-solid-svg-icons';
import '../style/collapse.scss';

            
export default function Collapse({ title, content}) {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <>
    <div className={` ${isOpen ? 'open' : ''}`}>
      <div className="bar" onClick={() => setIs(!toggle)} >
        <div className='title'>
          <h3>{title}</h3>
          <FontAwesomeIcon icon={faChevronUp} className={`arrow ${isOpen ? 'rotateDown' : 'rotateUp'}`} />
        </div>
      </div>
      <div className={`expandable-text ${isOpen ? 'expanded' : ''}`}> 
        {Array.isArray(content) ? content.map((title, id) => {
          return (
            <p key={id}>{title}</p>
          )
        }) : <p>{content}</p>}
      </div>
    </div>
    </>
  );
}
