import { Link, Redirect } from "react-router-dom";
import "../style/card&gallery.scss";
import data from '../data/data';



export default function Card({ id, title, cover, style }) {
  
  const data = data.map((data) => data.id);
  
    // Vérifiez si l'ID est reconnu
    const isIdRecognized = data.some(item => item.id === id);

    if (!isIdRecognized) {
      return <Redirect to="/Error" />;
    }
  return (
    <div className="card" style={style}>
      <Link to={`/Logements/${id}`}>
        <img src={cover} alt={title} style={{ objectFit: "cover" }} />
        <h3 className="card_title">{title}</h3>
      </Link>
    </div>
  );
}
