import React, { useState, useEffect } from 'react';

// Supposons que vos données sont importées à partir d'un fichier JSON comme ceci :
import data from '';

export default function Slider({ initialImageId }) {
  const initialIndex = data.findIndex(image => image.id === initialImageId);
  const [currentIndex, setCurrentIndex] = useState(initialIndex);

  useEffect(() => {
    const newIndex = data.findIndex(image => image.id === initialImageId);
    setCurrentIndex(newIndex);
  }, [initialImageId]);

  const nextSlide = () => {
    setCurrentIndex((currentIndex + 1) % data.length);
  }

  const prevSlide = () => {
    setCurrentIndex((currentIndex - 1 + data.length) % data.length);
  }

  return (
    <div className="slider">
      <button onClick={prevSlide}>Précédent</button>
      <img src={data[currentIndex].image} alt={data[currentIndex].title} />
      <button onClick={nextSlide}>Suivant</button>
    </div>
  );
}
