import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronUp } from '@fortawesome/free-solid-svg-icons';
import '../style/collapse.scss';

export default function Collapse({ title, content, extraClassName }) {
  const [toggle, setToggle] = useState(false);

  return (
    <div>
         <div className={`bar-container ${toggle ? 'open' : ''}${extraClassName}`}>
      <div className="bar" onClick={() => setToggle(!toggle)}>
        <div className='title'>
          <h3>{title}</h3>
          <FontAwesomeIcon icon={faChevronUp} className={`arrow ${toggle ? 'rotateDown' : 'rotateUp'}`} />
        </div>
      </div>
      <div className={`expandable-text ${toggle ? 'expanded' : ''}`}> 
        {Array.isArray(content) ? content.map((item, index) => {
          return (
            <p key={index}>{item}</p>
          )
        }) : <p>{content}</p>}
      </div>
    </div>
    </div>
 
  )
}
