import React, { useState, useEffect } from 'react';
import data from '../data/data';

export default function Slider({ initialImageId }) {
  const property = data.find(item => item.id === initialImageId);
  const images = property ? property.pictures : [];
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    setCurrentIndex(0);
  }, [initialImageId]);

  const nextSlide = () => {
    setCurrentIndex((currentIndex + 1) % images.length);
  }

  const prevSlide = () => {
    setCurrentIndex((currentIndex - 1 + images.length) % images.length);
  }

  return (
    <div className="slider">
      <button onClick={prevSlide}>Précédent</button>
      {images.length > 0 && <img src={images[currentIndex]} alt={property.title} />}
      <button onClick={nextSlide}>Suivant</button>
    </div>
  );
}
