import React, { useState } from 'react';


 function ExpandableBar({ title, children }) {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <div className="expandable-bar">
      <h2 onClick={() => setIsOpen(!isOpen)} className="title">
        {title} <span className="arrow">{isOpen ? '▼' : '►'}</span>
      </h2>
      {isOpen && <div className="content">{children}</div>}
    </div>
  );
}


export default function ExpBar() {
  return (
    <div className="exp-">
      <ExpandableBar title="Fiabilité">
        <p>Les annonces postées sur Kasa garantissent une fiabilité totale...</p>
      </ExpandableBar>
      <ExpandableBar title="Respect">
        <p>La bienveillance fait partie des valeurs fondatrices de Kasa...</p>
      </ExpandableBar>
      <ExpandableBar title="Service">
        <p>La sécurité est la priorité de Kasa...</p>
      </ExpandableBar>
    </div>
  );
}
