import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronDown, faChevronUp } from '@fortawesome/free-solid-svg-icons';



 export default function ExpandableBar({ title, content }) {

  const [toggle, setToggle] = useState(false);

  return (
<>
    <div className="expandable-bar">  
      <h3 className='title' onClick={() => setToggle(!toggle)}>
        {title}
        <FontAwesomeIcon icon={toggle ? faChevronDown : faChevronUp} className="arrow" />
      </h3>
      
    <div className={toggle ? 'expandable-text-' : 'expandable-text-hidden'}>
      {Array.isArray(content) ? content.map((item, index) => {
        return (
          <p key={index}>{item}</p>
        )
      }) : content
    }
    </div>
    </div>
    </>
  )
}
