import { useState } from 'react';
import Datas from '../data/data';
import '../style/Slider.scss';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronLeft, faChevronRight, faStar as fullStar } from '@fortawesome/free-solid-svg-icons';
import { faStar as emptyStar } from '@fortawesome/free-solid-svg-icons';
import { useParams } from 'react-router-dom';
import ExpandableBar from '../components/ExpandableBar';



export default function Slider() {

    

    const { id } = useParams();
    const dataCurrentAccomodation = Datas.find(data => data.id === id);

    const ids = Datas.map(data => data.id); // Créez un tableau d'identifiants
    const currentIndex = ids.indexOf(id); // Trouvez l'index de l'identifiant actuel

    const [index, setIndex] = useState(currentIndex); // Utilisez cet index pour le state initial
    
    const nextSlide = () => {
        setIndex((prevIndex) => (prevIndex + 1) % Datas.length);
    };

    const prevSlide = () => {
        setIndex((prevIndex) => (prevIndex - 1 + Datas.length) % Datas.length);
    };

    const Rating = ({ rating }) => {
        const totalStars = 5;
        let stars = [];

        for(let i = 0; i < totalStars; i++) { 
            if(i < rating) {
                stars.push(<FontAwesomeIcon className="star filled" key={i} icon={fullStar} />); // étoile remplie
            } else {
                stars.push(<FontAwesomeIcon className="star empty" key={i} icon={emptyStar} />); // étoile grise
            }
        }
        return <div className='rating'>{stars}</div>;
    };

    return (
        <body>
        <main>
            <div className='slider'>
                <img className= 'slider-img' src={Datas[index].cover} alt={dataCurrentAccomodation.title} />
                <div className='slider-title-container'>
                    <h2 id='title'>{Datas[index].title}</h2>
                    <p id='host'>{Datas[index].host.name}</p>
                </div>
                <p id='location'>{Datas[index].location}</p>
                <FontAwesomeIcon className='chevron chevron-left' icon={faChevronLeft} onClick={prevSlide} />
                <FontAwesomeIcon className='chevron chevron-right' icon={faChevronRight} onClick={nextSlide} />
                <div className='container-tag'>
                <div id='tags'>
                {Datas[index].tags.map((tag, i) => (
                    <p id='tag-name' key={i}>{tag}</p>
                ))}
                
                </div>
                <div> <Rating rating={Datas[index].rating} /></div>
                </div>
                <div className='bar-logements'>
                    <div id='description'><ExpandableBar  title='Description' content={Datas[index].description}/></div>
                    <div ><ExpandableBar title='Equipements' content={Datas[index].equipments}/></div>
               
                
                </div>
            </div>   
        </main>
        </body>
    );
}
